$(document).ready(function() {
    var myURL = "/" + location.pathname.split('/')[1];

    $('#mobile-menu li').each(function(){
        if ($(this).find('a').attr('href') == myURL) {
            $(this).find('a').addClass("selected")
            return
        }
    })
});

